action.script:
// Input validation utility
System.log("Validating input: " + input);

// Naming standards:
// - Must be 3-32 characters
// - Must start with letter
// - Can contain letters, numbers, hyphens
// - Cannot end with hyphen
var namePattern = /^[a-zA-Z][a-zA-Z0-9-]{2,31}$/;
var endsWithHyphen = /.-$/;

isValid = namePattern.test(input) && !endsWithHyphen.test(input);

if (isValid) {
  System.log("Input validation passed: " + input);
} else {
  System.log("Input validation failed: " + input);
  System.log("  Must be 3-32 characters, start with letter, contain only letters/numbers/hyphens");
}

return isValid;
