workflow-item.script:
// Create network segment using NSX-T API
var nsxClient = System.getModule("com.vmware.library.nsx").getNsxClient();

var segment = nsxClient.createSegment({
  displayName: segmentName,
  transport_zone_path: "/infra/sites/default/enforcement-points/default/transport-zones/overlay-tz"
});

System.log("Created segment: " + segment.displayName);

// Reference to Segment object type
var segment = new Segment();
segment.name = segmentName;

System.log("Attaching VM " + vmName + " to segment");
