workflow-item.script:
var nsxClient = System.getModule("com.vmware.library.nsx").getNsxClient();

// PARTIAL: segments (3 operations)
var webSegment = nsxClient.createSegment({
  displayName: "web-tier-segment"
});

var appSegment = nsxClient.createSegment({
  displayName: "app-tier-segment"
});

var dbSegment = nsxClient.createSegment({
  displayName: "db-tier-segment"
});

System.log("Created 3 network segments");

var nsxClient = System.getModule("com.vmware.library.nsx").getNsxClient();

// PARTIAL: firewall_rules (2 operations)
var webToAppRule = nsxClient.createFirewallRule({
  displayName: "web-to-app-allow",
  source_groups: ["web-tier"],
  destination_groups: ["app-tier"],
  services: ["HTTP", "HTTPS"],
  action: "ALLOW"
});

var appToDbRule = nsxClient.createFirewallRule({
  displayName: "app-to-db-allow",
  source_groups: ["app-tier"],
  destination_groups: ["db-tier"],
  services: ["MYSQL"],
  action: "ALLOW"
});

System.log("Created 2 firewall rules");

var nsxClient = System.getModule("com.vmware.library.nsx").getNsxClient();

// BLOCKED: security_groups (3 operations)
var webSG = nsxClient.createSecurityGroup({
  displayName: "web-tier-sg",
  members: []
});

var appSG = nsxClient.createSecurityGroup({
  displayName: "app-tier-sg",
  members: []
});

var dbSG = nsxClient.createSecurityGroup({
  displayName: "db-tier-sg",
  members: []
});

System.log("Created 3 security groups");
