workflow-item.script:
// Custom plugin: ServiceNow
var snow = System.getModule("com.vmware.library.servicenow").getConnection();

var changeRequest = snow.createChangeRequest({
  shortDescription: "Provision VM: " + vmName,
  requestedBy: "automation",
  category: "Infrastructure"
});

System.log("Created ServiceNow change request: " + changeRequest.number);

// Custom plugin: Infoblox IPAM
var infoblox = System.getModule("com.vmware.library.infoblox").getServer();

ipamRecord = infoblox.getNextAvailableIP({
  network: "10.0.0.0/24",
  hostname: vmName
});

System.log("Reserved IP: " + ipamRecord.ipAddress);

// Custom plugin: ServiceNow CMDB
var cmdb = System.getModule("com.vmware.library.servicenow").getCMDB();

cmdb.createCI({
  name: vmName,
  type: "Virtual Machine",
  ipAddress: ipamRecord.ipAddress,
  status: "Provisioning"
});

System.log("CMDB CI created for " + vmName);
